mapper.insert:
insert into album (name, image, url
  )
values (#{name,jdbcType=VARCHAR}, #{image,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}
  )

mapper.update:
update album
set name = #{name,jdbcType=VARCHAR},
  image = #{image,jdbcType=VARCHAR},
  url = #{url,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, image, url
from album
where id = #{id,jdbcType=BIGINT}

	 select id, name, image, url
  from album

  limit #{start},#{pageSize}
