mapper.insert:
insert into seo (seo_id, seo_title, seo_description, 
  seo_keywords, seo_type, seo_asid
  )
values (#{seoId,jdbcType=BIGINT}, #{seoTitle,jdbcType=VARCHAR}, #{seoDescription,jdbcType=VARCHAR}, 
  #{seoKeywords,jdbcType=VARCHAR}, #{seoType,jdbcType=VARCHAR}, #{seoAsid,jdbcType=BIGINT}
  )

mapper.select:
select seo_id, seo_title, seo_description, seo_keywords, seo_type, seo_asid
from seo
where seo_id = #{seoId,jdbcType=BIGINT}

sql.where:
seo_type=#{type}

	and seo_asid is null


	and seo_asid=#{asid}
